import { useTasks } from './Context'

export const Display = () => {
    const { todo , setTodo}  = useTasks()
    return(
        <>
            <ul>
            {
                todo.map((task,index) => {
                    return(
                        <li className = 'task__list'>
                            <p>
                                {index+1}
                                {".  "}
                                {task.description}
                                {"  "}
                                <button onClick={() => setTodo({type:'DELETE_TASK' , payload:{id:index}})}> 
                                    delete task 
                                </button>
                            </p>
                        </li>
                    )
                })
            }
            </ul>
        </>
    )
}
