import { useTasks } from './Context'
import { Button } from '@material-ui/core'
import { TiDeleteOutline } from '@'
export const Display = () => {
    const { todo , setTodo}  = useTasks()

    return(
        <>
            <ul className = 'list'>
            {
                todo.map((task,index) => {
                    return(
                        <li className = 'task__list'>
                            <p>
                                {index+1}
                                {".  "}
                                <div className='task'>
                                    {task.description}
                                </div>
                                {"  "}
                                <Button 
                                variant='contained' color='primary'
                                onClick={() => setTodo({type:'DELETE_TASK' , payload:{id:index}})}> 
                                    delete task 
                                </Button>
                            </p>
                        </li>
                    )
                })
        }
            </ul>
        </>
    )
}
