import { useTasks } from './Context'
import { useState } from 'react'
export const Input = () => {
    const {setTodo} = useTasks()
    const [text,setText] = useState('')
    return(
        <>
            <input 
            className='i'
            type='text' onChange = {(e) => setText(e.target.value)} />
            <button 
            onClick = { () => setTodo({type:'ADD_TASK' , payload:{description:text}}) }>
                add task
            </button>
        </>
    )
}
