import { useState } from 'react'

export const Time = () => {

    const [time,setTime] = useState('')
    function getTime () {
        const today = new Date()
        const now = today.toString()
        setTime(now)
        setTimeout(getTime , 1000)
    }

    function printTime () {
        getTime()
        return <div>{ time }</div>
    }

    return(
        <div>
            {
                printTime()
            }
        </div>
    )

}
