import { useEffect, useState } from 'react'
import { useLogin } from './Context'
export const Time = () => {
    const { setLastLogin} = useLogin()
    const [time,setTime] = useState(new Date().toString())


    setInterval(() => {
        const today = new Date()
        setTime(today.toString())
    },1000)
    return(
        <div>
            {time}
        </div>
    )

}
